$(function(){
    
    $('head').append(`<meta property="og:description"   content="hellow world"/>`);
    
    AOS.init();

    $('.nav--lists a').on('click', function (e) {
        var href=$(this).attr('href');
        $('.header-inner').toggleClass('nav-open');
        $('.mainnav').toggleClass('nav-open');
        var ccHeight = $("header").height();
        e.preventDefault();
        $('html, body').animate({
            scrollTop: $(href).offset().top - ccHeight
        }, 500, 'linear');
    });
    $(window).scroll(function() {
        var scrollDistance = $(window).scrollTop() + $("header").height();
        // Assign active class to nav links while scolling
        $('.page-section').each(function(i) {
            if ($(this).offset().top - $("header").height() <= scrollDistance) {
                $('.nav--lists a.active').removeClass('active');
                $('.nav--lists a').eq(i).addClass('active');
            }
        });
    }).scroll();

    $('.gamburg-btn').on('click', function () {
        $('.header-inner').toggleClass('nav-open');
    });
    $('.gamburg-btn').on('click', function () {
        $('.mainnav').toggleClass('nav-open');
    });

    $('.app-menu-toggle').on('click', function (e) {
        $(this).toggleClass('open');
        if ($(this).hasClass('open')) {
            $('.app-left-sidebar').addClass('is-active');
        } else {
            $('.app-left-sidebar').removeClass('is-active');
        }
        e.stopPropagation();
    });

    $('.app-page').on('click', function () {
        $('.app-menu-toggle').removeClass('open');
        $('.app-left-sidebar').removeClass('is-active');
    });
    $('#profilePDF').on('click', function () {
        console.log( window.location.pathname);
//             window.location.href = window.location.pathname + "/pdf/susant-cv.pdf";
        });




});
