$(function(){
    AOS.init();


    $('a[href*="#home"]').on('click', function (e) {
        var ccHeight = $("header").height();
        e.preventDefault();
        $('html, body').animate({
            scrollTop: $("#home").offset().top - ccHeight
        }, 500, 'linear');
    });
    $('a[href*="#about"],.scroll-down').on('click', function (e) {
        var ccHeight = $("header").height();
        e.preventDefault();
        $('html, body').animate({
            scrollTop: $("#about").offset().top - ccHeight
        }, 500, 'linear');
    });
    $('a[href*="#skills"]').on('click', function (e) {
        var ccHeight = $("header").height();
        e.preventDefault();
        $('html, body').animate({
            scrollTop: $("#skills").offset().top - ccHeight
        }, 500, 'linear');
    });
    $('a[href*="#works"]').on('click', function (e) {
        var ccHeight = $("header").height();
        e.preventDefault();
        $('html, body').animate({
            scrollTop: $("#works").offset().top - ccHeight
        }, 500, 'linear');
    });
    $('a[href*="#contact"]').on('click', function (e) {
        var ccHeight = $("header").height();
        e.preventDefault();
        $('html, body').animate({
            scrollTop: $("#contact").offset().top - ccHeight
        }, 500, 'linear');
    });

    $(window).scroll(function() {
        var scrollDistance = $(window).scrollTop() + $("header").height();
        // Assign active class to nav links while scolling
        $('.page-section').each(function(i) {
            if ($(this).offset().top - $("header").height() <= scrollDistance) {
                $('.nav--lists a.active').removeClass('active');
                $('.nav--lists a').eq(i).addClass('active');
            }
        });
    }).scroll();
})();


